    const volumeInfo = item.volumeInfo || {};
    const title = volumeInfo.title || '';
    if (title.toLowerCase().includes(char.toLowerCase())) {
      return true;
    }
  }
  return false;
};

const getBookInfo = (data, searchQuery) => {
  const items = data.items || [];
